<script type="text/javascript">
  $(document).ready(function() {
// add input author from field #########################################################################################################################################################
    var i = 1;
    $('#addif').click(function() {
      i++;
      $('#dynamic_field').append('<tr id="row'+i+'"><td class="text" style="padding-left: 25px;">AUTHOR&nbsp;&nbsp;</td><td><input type="text" id="'+i+'" name="name[]" placeholder="Kramer, James D; Chen, Jacky" class="form-control name_list" /></td><td><button type="button" name="remove" id="' + i + '" class="btn btn_remove"><img src="data:image/svg+xml;base64," style="width: 30px; height: 30px; display: inline-block; padding-left: 0px;"/></button></td></tr>');
    });
// add delete input author from field ###########################################################################################################################################################
    $(document).on('click', '.btn_remove', function() {
      var button_id = $(this).attr("id");
      $('#row' + button_id + '').remove();
    });

// save data from #insertModal to database ######################################################################################################################################################
    $('#insert-form').on('submit', function(e){ 
      e.preventDefault();           
           $.ajax({  
                url:"insert.php",  
                method:"POST",  
                data:$('#insert-form').serialize(),  
                beforeSend:function(){
                  $('#insertSubmit').val("Insert...");
                },
                success:function(data)  
                {  
                    //  alert(data);  
                     $('#insert-form')[0].reset();  
                     $('#insertModal').modal('hide');
                    //  $('#add_name').html(data);  

                    location.reload();
                    //  location.reload();
                }  
           });  
    }); 
// hide/show radio input url/doi ######################################################################################################################################################
    $('#Ddoi').hide();
    $('#Durl').show();

    $('input[type=radio][name=options]').change(function() {
      if (this.id == "optionURL") {
        $('#Ddoi').hide();
        $('#Durl').show();
      }else if (this.id == "optionDOI") {
        $('#Durl').hide();
        $('#Ddoi').show();
      }
    });
// hide/show select option input journal_name/periodical_name ######################################################################################################################################################
    $('#Djournal_name').show();
    $('#Dperiodical_name').hide();

    $('select[name=selectTypeOfSource]').change(function () {
      $("select[name=selectTypeOfSource] option:selected").each(function () {
          var value = $(this).val();
          if(value == "journal_article") {
              $('#Djournal_name').show();
              $('#Dperiodical_name').hide();
          } else if(value == "article_periodical") {
              $('#Djournal_name').hide();
              $('#Dperiodical_name').show();
          } 
      });
    }); 
// select style bibliography ######################################################################################################################################
    $('#apa').show();
    $('#mla').hide();
    $('#chicago').hide();

    $('select[name=bibliographyStyle]').change(function () {
      $("select[name=bibliographyStyle] option:selected").each(function () {
          var value = $(this).val();
          if(value == "apa") {
            $('#apa').show();
            $('#mla').hide();
            $('#chicago').hide();
          } else if(value == "mla") {
            $('#apa').hide();
            $('#mla').show();
            $('#chicago').hide();
          }  else if(value == "chicago") {
            $('#apa').hide();
            $('#mla').hide();
            $('#chicago').show();
          } 
      });
    }); 
// edit data from #insertModal to database ########################################################################################################################################################
  $('.edit_data').click( function(){  
      // var uid = $('#postid').val();
      var uid = $(this).attr("id");  
      $.ajax({  
        url:"fetch.php",  
        method:"POST",  
        data:{id: uid},  
        dataType:"json",  
        success:function(data){  
          $('#hidden_id').val(data.id);
          $('#name').val(data.name);  
          $('#titleB').val(data.title);  
          $('#journal_nameB').val(data.journal_name);  
          $('#periodical_nameB').val(data.periodical_name); 
          $('#dayPB').val(data.dayP);  
          $('#monthPB').val(data.monthP); 
          $('#yearPB').val(data.yearP);  
          $('#page_startB').val(data.page_start); 
          $('#page_endB').val(data.page_end);  
          $('#volumeB').val(data.volume); 
          $('#issueB').val(data.issue);  
          $('#urlB').val(data.url);  
          $('#doiB').val(data.doi); 
          $('#insertSubmit').val("Update");
          $('#insertModal').modal('show');
        }  
      });  
  });
